import React from 'react';
import {set_JSON, set_plain, get_JSON, get_plain} from '../store.js';

export default class Simple extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: get_plain("state") || null
    };
    this.handlePress = this.handlePress.bind(this);
  }

  handlePress(event) {
    if (event.key === 'Enter') {
      this.setState({
        name: this.refs.term.value
      });
      set_plain("state", this.state.name);
    }
  }

  render() {
    return (
      <div>
        <input type="text" onKeyPress={this.handlePress} ref="term" placeholder={this.state.name}/>
      </div>
    );
  }
}
